function getMarkdownUrl(){const e=window.location.pathname;return e.endsWith("/")?e+"index.md":e+".md"}function fetchMarkdownContent(){const e=getMarkdownUrl();return fetch(e).then(e=>{if(!e.ok)throw new Error("Markdown file not found");return e.text()})}function copyMarkdown(){fetchMarkdownContent().then(e=>{navigator.clipboard.writeText(e).then(()=>{showNotification("page copied as markdown to clipboard")}).catch(e=>{showNotification("failed to copy: "+e)})}).catch(e=>{showNotification("error: "+e.message)})}function viewMarkdown(){const e=getMarkdownUrl();window.open(e,"_blank")}function showNotification(e){const t=document.createElement("div");t.textContent=e,t.style.cssText=`
        position: fixed;
        top: 20px;
        right: 20px;
        background: var(--flexoki-dark-cyan);
        color: var(--flexoki-50);
        padding: 12px 16px;
        border-radius: 4px;
        font-size: 14px;
        z-index: 10000;
        box-shadow: 0 2px 8px rgba(0,0,0,0.2);
        animation: fadeInOut 2s ease-in-out;
    `;const n=document.createElement("style");n.textContent=`
        @keyframes fadeInOut {
            0% { opacity: 0; transform: translateY(-10px); }
            10% { opacity: 1; transform: translateY(0); }
            90% { opacity: 1; transform: translateY(0); }
            100% { opacity: 0; transform: translateY(-10px); }
        }
    `,document.head.appendChild(n),document.body.appendChild(t),setTimeout(()=>{t.parentNode&&t.parentNode.removeChild(t),n.parentNode&&n.parentNode.removeChild(n)},2e3)}document.addEventListener("keydown",function(e){if((e.ctrlKey||e.metaKey)&&e.key==="c"){const t=window.getSelection();t.toString().trim()===""&&(e.preventDefault(),copyMarkdown())}})